mapper.update:
update news
set title = #{title}, cnts = #{cnts}
where news_no = #{news_no}
and mno = #{mno}

update qna
set title = #{title}, qna_cnts = #{cnts}
where qna_no = #{qna_no}
and mno = #{mno}

mapper.delete:
delete from news
where news_no = #{news_no}
and mno = #{mno}

delete from qna
where qna_no = #{qna_no}
and mno = #{mno}

mapper.update:
update qna
set view_cnt = view_cnt + 1
where qna_no = #{qna_no}

update news
set view_cnt = view_cnt + 1
where news_no = #{news_no}
